import { createSlice } from '@reduxjs/toolkit';
const initialState = [];

export const listSlice = createSlice({
    name: 'list',
    initialState,
    reducers:
    {
        createTodo: (state, action) => {
            state.push(action.payload)
        },
        deleteTodo: (state, action) => {
            return state.filter(task => task.id != action.payload.id)
        },
        toggleTodo: (state, action) => {
            const currentIndex = state.findIndex(task => task === action.payload);

            state[currentIndex].done = !action.payload.done;
            // state = [...state.slice(0, currentIndex),
            // { ...action.payload, done: !action.payload.done },
            // ...state.slice(currentIndex + 1)]
        },
        fetchList: (action) => {
            return action.payload
        }
    }
});

export const { createTodo, deleteTodo, toggleTodo, fetchList } = listSlice.actions;
export default listSlice.reducer;
